import React, { Component } from 'react';

const Educ = ({education}) => {
  return (
    <div class="resume-item d-flex flex-column flex-md-row justify-content-between mb-5">
      <div class="resume-content">
        <h3 class="mb-0">{education.school}</h3>
        <div class="subheading mb-3">{education.degree}</div>
        <div>{education.subject}</div>

      </div>
      <div class="resume-date text-md-right">
        <span class="text-primary">{education.dates}</span>
      </div>
    </div>
  )
}

const educations = [
  {
    school: 'Université de Nantes',
    degree: "Licence",
    subject: "Sciences de l'Éducation",

    dates: 'Septembre 2008 - Juin 2009'
  },
  {
    school: 'Université de Nantes',
    degree: "Licence",
    subject: "Sciences de l'Éducation",

    dates: 'Septembre 2008 - Juin 2009'
  },
  {
    school: 'James Buchanan High School',
    degree: 'Technology Magnet Program',
    subject: '',
    gpa: 'GPA: 3.56',
    dates: 'August 2002 - May 2006'
  }
]

export default class Education extends Component {
  render() {
    return (
    <section class="resume-section p-3 p-lg-5 d-flex align-items-center" id="education">
      <div class="w-100">
      {educations.map(educ => {
            return (<Educ education={educ} />)
          })}
      </div>
    </section>
    )
  }
}
